mapper.select:
SELECT id, `name`, t.caption, t.image, t.price, t.market_price, t.sales, t.score, t.create_date, t.is_top FROM xx_goods t
( `name` LIKE '%${keyword}%' OR introduction LIKE '%${keyword}%' OR sn LIKE '%${keyword}%' OR keyword LIKE '%${keyword}%' )
AND price >= #{startPrice}
AND price <= #{endPrice}
AND brand = #{brandId}

SELECT
g.id, g.`name`, g.caption, g.image, g.price, g.market_price,
g.sales, g.score, g.create_date, g.is_top
FROM
xx_goods g
LEFT JOIN xx_product_category p ON g.product_category = p.id

	(p.tree_path LIKE '${treePath}%' or p.id = #{productCategoryId})
	
		AND (
		`name` LIKE '%${keyword}%'
		OR introduction LIKE '%${keyword}%'
		OR sn LIKE '%${keyword}%'
		OR keyword LIKE '%${keyword}%'
		)
	
	
		AND price >= #{startPrice}
	
	
		AND price <= #{endPrice}
	
	
		AND g.brand = #{brandId}
	
	
		AND g.attribute_value0 = #{attributeValue0}
	
	
		AND g.attribute_value1 = #{attributeValue1}
	
	
		AND g.attribute_value2= #{attributeValue2}
	
	
		AND g.attribute_value3 = #{attributeValue3}
